drop table if exists UserJobs;drop table if exists Jobs;drop table if exists Users;create table Jobs(	JobId int primary key,	Name varchar(100) not null);create table Users(	UserId int primary key,	Username varchar (50) not null,	Lastname varchar(50) not null);create table UserJobs(	UserId int,	JobId int,	DateTimeCreated datetime not null,	constraint fk_user foreign key (UserId) references Users(UserId),	constraint fk_job foreign key (JobId) references Jobs(JobId),	primary key (UserId, JobId, DateTimeCreated))create index idx1 on UserJobs(DateTimeCreated);