declare @n int = 2000declare @i int = 0while @i < @n begin    SET @i = @i + 1    insert into [uzduotis].[dbo].[Jobs]	values	(@i, concat('jobname', @i))endcommit;set @i = 0while @i < @n begin    SET @i = @i + 1    insert into [uzduotis].[dbo].[Users]	values	(@i, concat('userName', @i), concat('lastName', @i))endcommit;set @i = 0--declare @time DateTime ='2026-01-20 11:28:32.463';declare @time DateTime = getdate()while @i < @n begin    SET @i = @i + 1	declare @j int = 0;	while @j<@i	begin		SET @j=@j+1		insert into [uzduotis].[dbo].[UserJobs]		values		(@i, @j, @time)		set @time = DATEADD(hh, -1, @time)	endendcommit;